mapper.update:
UPDATE TB_PRD
SET
	DT_UPT = SYSDATE
	, UPDATER = #{register}
	
	, PRD_NM = #{prd_nm}
	
	
	, CD_CTG_PET = #{cd_ctg_pet}
	
	
	, PRICE_COST = #{price_cost}
	
	
	, COUNT_STOCK = #{count_stock}
	
	
	, CD_STATE_PRD = #{cd_state_prd}
	
WHERE
	SEQ_PRD = #{seq_prd}

