mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY sle.DT_REG DESC) rnum , sle.SEQ_SLE , sle.SLE_NM , sle.SPECIES , sle.PET_ITEMS , sle.DESCES , sle.CD_STATE_SALE , sle.PRICE_SALE , TO_CHAR(sle.DT_REG, 'YYYY-MM-DD') dt_reg FROM TB_SLE sle INNER JOIN TB_PRD prd ON sle.SEQ_PRD = prd.SEQ_PRD WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum

mapper.sql:
	sle.REGISTER = #{register}

	
		AND UPPER(sle.SLE_NM) LIKE UPPER('%' || #{searchWord} || '%')
	
	
		AND UPPER(sle.DESCES) LIKE UPPER('%' || #{searchWord} || '%')
	
	
		AND (
			UPPER(sle.DESCES) LIKE UPPER('%' || #{searchWord} || '%')
			OR
			UPPER(sle.SLE_NM) LIKE UPPER('%' || #{searchWord} || '%')
		)
	


	
		AND sle.CD_STATE_SALE = #{cd_state_sale}
	
	
		AND sle.CD_STATE_SALE IN (1, 2, 9)
