mapper.update:
UPDATE
	TB_SLE
SET
	CD_STATE_SALE	= 9
	, DT_UPT		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	REGISTER		= #{register}
	AND SEQ_SLE		= #{seq_sle}

UPDATE
	TB_SLE
SET
	CD_STATE_SALE	= 1
	, DT_UPT		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	REGISTER		= #{register}
	AND SEQ_SLE		= #{seq_sle}

UPDATE
	TB_SLE
SET
	CD_STATE_SALE	= 2
	, DT_UPT		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	REGISTER		= #{register}
	AND SEQ_SLE		= #{seq_sle}

UPDATE
	TB_SLE
SET
	SLE_NM			= #{sle_nm}
	, DESCES 		= #{desces}
	, SPECIES 		= #{species}
	, PET_ITEMS 	= #{pet_items}
	, PRICE_SALE	= #{price_sale}
	, CD_STATE_SALE	= #{cd_state_sale}
	, DT_SALE_START	= #{dt_sale_start}
	, DT_SALE_END	= #{dt_sale_end}
	, DT_REG		= SYSDATE
	, UPDATER		= #{updater}
WHERE
	REGISTER		= #{register}
	AND SEQ_SLE		= #{seq_sle}

mapper.select:
SELECT * FROM ( SELECT /*+ first_rows(10) */ ROW_NUMBER() OVER (ORDER BY sle.DT_REG DESC) rnum , sle.SEQ_SLE , sle.SLE_NM , sle.SPECIES , sle.PET_ITEMS , sle.DESCES , sle.CD_STATE_SALE , sle.PRICE_SALE , TO_CHAR(sle.DT_REG, 'YYYY-MM-DD') dt_reg FROM TB_SLE sle INNER JOIN TB_PRD prd ON sle.SEQ_PRD = prd.SEQ_PRD WHERE
)
WHERE rnum >= (${currentPage} - 1) * ${linePerPage} + 1 AND rnum <= ${currentPage} * ${linePerPage}
ORDER BY rnum

mapper.sql:
	sle.REGISTER = #{register}

	AND SPECIES = #{species}


	
		AND SLE_NM LIKE '%' || #{searchWord} || '%'
	
	
		AND SLE_DESCES LIKE '%' || #{searchWord} || '%'
	
	
		AND (SLE_NM LIKE '%' || #{searchWord} || '%' OR DESCES LIKE '%' || #{searchWord} || '%')
	


	
		AND sle.CD_STATE_SALE = #{cd_state_sale}
	
	
		AND sle.CD_STATE_SALE IN (1, 2, 9)
